<?php require_once '../config.php'; if(!isset($_SESSION['user_id'])){ header('Location: login.php'); exit; } $products=$pdo->query('SELECT p.*, u.name as seller FROM products p LEFT JOIN users u ON u.id=p.seller_id ORDER BY p.id DESC')->fetchAll(); ?> <!doctype html><html lang="fr"><head><meta charset="utf-8"><title>GÃ©rer produits</title></head><body><h2>Produits</h2><table border="1" cellpadding="6"><tr><th>ID</th><th>Titre</th><th>Prix</th><th>Stock</th><th>Vendeur</th></tr><?php foreach($products as $p){ echo "<tr><td>{$p['id']}</td><td>{$p['title']}</td><td>{$p['price']}</td><td>{$p['stock']}</td><td>{$p['seller']}</td></tr>"; } ?></table><p><a href="dashboard.php">Retour</a></p></body></html>